mapper.select:
select * from(
select * from(
	select rownum as rn, p.* from
	((select * from notice where title like '%'||#{param2}||'%' order by nseq desc)p)
) where rn >=#{param1.startNum}
) where rn <=#{param1.endNum}

select * from (
select * from (
	select rownum as rn, m.*from(( select * from lmember 
	where name like '%' || #{param2} || '%' 
	order by indate  desc) m)
) where rn > = #{param1.startNum}
) where rn < = #{param1.endNum}

select * from (
select * from (
	select rownum as rn, at.*from(( select * from attraction 
	where atname like '%' || #{param2} || '%' 
	order by aseq  desc) at)
) where rn > = #{param1.startNum}
) where rn < = #{param1.endNum}

mapper.update:
update attraction set atname=#{atname}, acontent=#{acontent}, act1=#{act1}, act2=#{act2},
 image=#{image} ,pnum=#{pnum}, limitkey=#{limitkey}, limitage=#{limitage}, 
 bestat=#{bestat}, aresult=#{aresult} where aseq=#{aseq}

mapper.select:
select * from (
select * from (
	select rownum as rn, q.*from(( select * from lqna 
	where title like '%' || #{param2} || '%' 
	order by lqseq desc) q)
) where rn > = #{param1.startNum}
) where rn < = #{param1.endNum}
