mapper.update:
Update Lmember set pwd=#{pwd}, name=#{name}, zip_num= #{zip_num}, 
address1=#{address1},	 address2=#{address2},	
email=#{email}, phone=#{phone} where id = #{id}

mapper.select:
select * from ( 
select * from(
	select rownum as rn, p.* from 
	((select * from cart2   where cseq like '%'||#{param2}||'%'   order by cseq desc) p )
) where rn >= #{param1.startNum} 
) where rn <= #{param1.endNum}
